// Pega a data atual
const hoje = new Date();

// Lista com os dias da semana em português (começando no domingo)
const diasSemana = ['DOMINGO', 'SEGUNDA', 'TERÇA', 'QUARTA', 'QUINTA', 'SEXTA', 'SÁBADO'];

// Obtém o dia da semana atual (0 = domingo, 1 = segunda, etc.)
const diaSemana = diasSemana[hoje.getDay()];

// Retorna o valor como saída do node
return [
  {
    json: {
      dia_da_semana: diaSemana,
      data_completa: hoje.toISOString(),
    },
  },
];
